from unittest import TestCase
import boto3
import moto

import SampleResourceInventoryEvaluationScripts.war_runner.reliability.evaluate_asg_services as asgr
import SampleResourceInventoryEvaluationScripts.war_runner.common.war_service as war


class Testevaluate_asg_services(TestCase):
    def test_setup(self):
        a = asgr.evaluate_asg_services()
        asg_groups = a.setup( "us-east-1")
        assert len(asg_groups['AutoScalingGroups']) == 2


    def test_evaluate(self):
        a = asgr.evaluate_asg_services()
        asg_groups = a.setup("us-east-1")
        responses = a.evaluate(asg_groups, ["Product"])
        self.assertEquals(2, len(responses))

