from unittest import TestCase


import SampleResourceInventoryEvaluationScripts.war_runner.reliability.evaluate_elbv2_services as elb


class Testevaluate_elbv2_services(TestCase):
    def test_setup(self):
        e = elb.evaluate_elbv2_services()
        elbs = e.setup(region="us-east-1")
        self.assertEquals( 2, len(elbs['LoadBalancers']))

    def test_evaluate(self):
        e = elb.evaluate_elbv2_services()
        elbs = e.setup(region="us-east-1")
        responses = e.evaluate(elbs, ["Product"])
        self.assertEquals(0, len(responses))

