from unittest import TestCase
import SampleResourceInventoryEvaluationScripts.war_runner.reliability.evaluate_kinesis_services as kds

class Testevaluate_kinesis_services(TestCase):

    def test_setup(self):
        k =kds.evaluate_kinesis_services()
        kds_streams = k.setup("us-east-1")
        self.assertEquals(2, len(kds_streams['StreamNames']))

    def test_evaluate(self):
        k =kds.evaluate_kinesis_services()
        kds_streams = k.setup("us-east-1")
        responses = k.evaluate(kds_streams, ['Product'])

        self.assertEquals(1, len(responses))


