from unittest import TestCase

import SampleResourceInventoryEvaluationScripts.war_runner.reliability.evaluate_rds_services as rds


class Testevaluate_rds_services(TestCase):
    def test_setup(self):
        r = rds.evaluate_rds_services()
        rds_db = r.setup("us-east-1")
        self.assertEquals(1,len(rds_db))

    def test_evaluate(self):
        r = rds.evaluate_rds_services()
        rds_db = r.setup("us-east-1")
        responses = r.evaluate(rds_db, ["Product"])
        self.assertEquals(1,len(responses))
