from unittest import TestCase
import SampleResourceInventoryEvaluationScripts.war_runner.reliability.evaluate_vpc_services as vpc

class Testevaluate_vpc_services(TestCase):
    def test_setup(self):
        v = vpc.evaluate_vpc_services()
        vpc_list = v.setup("us-east-1")
        self.assertEquals(6, len(vpc_list))

    def test_evaluate(self):
        v = vpc.evaluate_vpc_services()
        vpc_list = v.setup("us-east-1")
        responses = v.evaluate(vpc_list)
        self.assertEquals(0, len(responses))
